import (
"fmt"
"strings"
)
func upone_list(thing []string, index int) {
thing[index] = strings.ToUpper(thing[index])
}
func upone_map(thing map[string]string, index string) {
thing[index] = strings.ToUpper(thing[index])
}
func main() {
// mutable
list := []string{"a", "b", "c"}
upone_list(list, 1)
