</code></pre> <pre><code>package main
import (
"fmt"
"math"
)
type Point struct {
x float64
y float64
}
func (this Point) distance(other Point) float64 {
return math.Sqrt(this.x*other.x + this.y*other.y)
}
// Dince结构会自动复制，
//最好将其作为指针传递。
func (this *Point) distance_better(other *Point) float64 {
return math.Sqrt(this.x*other.x + this.y*other.y)
}
func main() {
p1 := Point{
x: 1,
y: 3,
}
p2 := Point{
x: 2,
y: 4,
}
fmt.Println(p1.distance(p2))            //3.7416573867739413
fmt.Println(p1.distance_better(&amp;p2))    //3.7416573867739413
}
</code></pre>

PythonGo <pre><code>#!/usr/bin/env python
# -*- coding:utf8 -*-
def uniqify(seq):
seen = {}
unique = []
# 遍历序列，如果不在字典中，先加入字典，加入列表，在字典中的为重复的，不加入列表
for item in seq:
if item not in seen:
seen[item] = 1
unique.append(item)
return unique
items = ['B', 'B', 'E', 'Q', 'Q', 'Q']
print(uniqify(items))  # prints ['B', 'E', 'Q']
</code></pre> <pre><code>package main
import "fmt"
func uniqify(items *[]string) {
seen := make(map[string]bool)
j := 0
for i, x := range *items {
if !seen[x] {
seen[x] = true
(*items)[j] = (*items)[i]
j++
}
}
*items = (*items)[:j]
}
func main() {
items := []string{"B", "B", "E", "Q", "Q", "Q"}
uniqify(&amp;items)
fmt.Println(items) // prints [B E Q]
}
</code></pre>

